package day06;

public class Assign_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		int b=5;
		
		b=a; //b=3
		System.out.println(b);
		
		a+=1; //a=a+1
		System.out.println(a); //a=4
		
		a/=2; //a=4/2
		System.out.println(a);
		
		a*=a; //a=a*2
		System.out.println(a); //a=4

	}

}
