package day10;

public class While02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=100;
		do {
			System.out.println("적어도 한번은 출력되는 문장");
			
		}while(i<10);
		System.out.println("프로그램 종료");
		
		
		while(i<10) {
			System.out.println("적어도 한번은 출력되는 문장");
		}
		System.out.println("프로그램 종료");
	}

}
